import React, { Component } from 'react'

import { Dropdown } from 'primereact/dropdown';

import './SaveOcrResultButton.css'



export default class SaveOcrResultButton extends Component {

    constructor(props) {
        super(props);

        this.items = [
            {
                label: 'Plain Text (txt)',
                extension: '.txt',
                type: 'text/plain',
            },
            {
                label: 'Portable Document (pdf)',
                extension: '.pdf',
                type: 'application/pdf'
            },
            {
                label: 'Microsoft Word (docx)',
                extension: '.docx',
                type: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
            },
            {
                label: 'OpenOffice (odf)',
                extension: '.odf',
                type: 'application/vnd.oasis.opendocument.text'
            },
        ];

        this.state = {
            downloadLink: "",
        }
    }

    onSelectionChange = (e) => {
        this.createFile(e.value.type)
        this.downloadFile(e.value.extension)
    }

    createFile = (fileType) => {
        const data = new Blob(["this.state.result"], {type: fileType})
        window.URL.revokeObjectURL(this.state.downloadLink)
        this.setState({downloadLink: window.URL.createObjectURL(data)})
    }

    downloadFile = (fileExtension) => {
        const a = document.createElement('a');
        document.body.appendChild(a);

        a.href = this.state.downloadLink;

        a.download = "ocrresult".concat(fileExtension);
        a.click();

        window.URL.revokeObjectURL(fileUrl);
        a.remove();
    }


    render() {
        return (
            <div>
                <Dropdown placeholder="Download" options={this.items} optionlabel="label" onChange={this.onSelectionChange} />
            </div>
        )
    }
}
