import React, {useEffect, useState} from 'react'
import upload_logo from '../../assets/images/add_file_v2.svg';
import './UploadImage.css'

import { Button } from 'primereact/button';
import Dropzone, {useDropzone} from 'react-dropzone';


export default function UploadImage(props) {

    const [files, setFiles] = useState([]);
    const {acceptedFiles, getRootProps, getInputProps} = useDropzone({
        maxFiles: 1,
        accept: 'image/*',
        onDrop: acceptedFiles => {
        setFiles(acceptedFiles.map(file => Object.assign(file, {
            preview: URL.createObjectURL(file)
        })));
        acceptedFiles.forEach((file) => {
            const reader = new FileReader()

            reader.onabort = () => console.log('file reading was aborted')
            reader.onerror = () => console.log('file reading has failed')
            reader.onload = () => {
            // Do whatever you want with the file contents
                const binaryStr = reader.result
                console.log(binaryStr)
            }
            reader.readAsArrayBuffer(file)
        });
        }
    });

    const thumbs = files.map(file => (
        <div  key={file.name}>
          <div >
            <img
              src={file.preview}
            />
          </div>
        </div>
      ));

      useEffect(() => () => {
        // Make sure to revoke the data uris to avoid memory leaks
        files.forEach(file => URL.revokeObjectURL(file.preview));
      }, [files]);


    return (
        <div className="upload-container">
            <div className="upload-area">
            <section>
                <div {...getRootProps({className: 'dropzone'})}>
                    <input {...getInputProps()} />
                    <img className="upload-image-logo" src={upload_logo}></img>
                    <p style={{fontWeight: 'bold', fontSize: '16px'}}>Drag 'n' drop some files here, or click to select files</p>
                </div>
            </section> 
            </div>
            <div className="scan-button-container">
                <Button className="scan-button">Scan Document</Button>
            </div>
            <aside>
                {thumbs}
            </aside>
        </div>
        )
    }
